import Image from 'next/image'
import Link from 'next/link'
import css from 'styled-jsx/css'

const Header = () => (
  <header className="site-header">
    <Link href="/">
      <a>
        <Image
          src="/fakefurniture.png"
          alt="Fake Furniture Logo"
          width={250}
          height={50}
        />
      </a>
    </Link>
    <nav>
    <ul>
      <li>
        <a href="/account">Accounts</a>
      </li>
      <li>
        <a href="/products">Products</a>
      </li>
      <li>
        <Link href="/about">
          <a>About us</a>
        </Link>
      </li>
      </ul>
  </nav>
  <style jsx global>{ headerStyles }</style>
</header>
)

export default Header

export const headerStyles = css.global`
.site-header {
  height: 30px;
  padding: 20px 0;
}
.site-header,
.site-header a {
  color: #484d52;
  font-weight: 700;
}
.site-header nav a:hover {
  color: #72767B;
}
.homepage .site-header,
.homepage .site-header a {
  color: #ffffff;
}
.homepage .site-header nav a:hover {
  color: #c8c9cb;
}
.site-header .logo {
  display: inline-block;
  font-size: 22px;
  font-weight: 900;
}
.site-header nav {
  float: right;
}
.site-header nav ul {
  margin: 0;
}
.site-header nav li {
  display: inline-block;
  margin-left: 40px;
}
@media (max-width: 1060px) {
  .site-header {
    padding-left: 20px;
    padding-right: 20px;
  }
}
@media (max-width: 767px) {
  .site-header {
    height: auto;
  }
  .homepage .site-header {
    position: absolute;
    left: 0;
    right: 0;
  }
  .site-header .logo {
    display: block;
    text-align: center;
  }
  .site-header nav {
    float: none;
    text-align: center;
  }
  .site-header nav li {
    display: inline-block;
    margin-left: 10px;
    margin-right: 10px;
  }
}
`
